mapper.insert:

insert into bd_qna values( seq_bd_qna.nextval,  #{userNo },  #{title },  #{content }, sysdate, '0',
							#{groupNo },
							#{orderNo } + 1,
							#{depth } + 1,
							#{category}
							)

mapper.update:

update bd_qna set 
order_no = order_no + 1
where group_no = #{groupNo }
and order_no > #{orderNo}
