mapper.update:

update coupon 
set count = count-1 
where USER_NO = #{userNo} and PROGRAM_NO = #{programNo}


update coupon 
set count = #{count}
where USER_NO = #{userNo} and PROGRAM_NO = #{programNo}


update coupon 
set count = 0
where USER_NO = #{userNo} and PROGRAM_NO = #{programNo}

mapper.insert:

insert into sales
select seq_sales.nextval, user_no, pay_no, program_no, reg_date, -price 
from sales where no=#{no}
